package sample;

public class Employee {
	
	String name;
	int salary;
	
	public Employee(String naam, int paise) {
		// TODO Auto-generated constructor stub
		this.name = naam;
		this.salary = paise;
	}

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}

	public int getSalary() {
		return salary;
	}

	public void setSalary(int salary) {
		this.salary = salary;
	}
	

}
